// ==ClosureCompiler==
// @compilation_level SIMPLE_OPTIMIZATIONS
// ==/ClosureCompiler==
/*global window */
/*jslint maxlen: 80 */
(function (window) {
    "use strict";

    (function (shortname, category, identifier, title, url) {
        var disqus_category_id = category,
            disqus_identifier = identifier,
            disqus_shortname = shortname, // Required forum shortname
            disqus_title = title,
            disqus_url = url,
            parent = (document.getElementsByTagName("head")[0] ||
                document.getElementsByTagName("body")[0]),
        script = document.createElement("script");
        script.defer = true;
        script.src = "https://" + disqus_shortname + ".disqus.com/embed.js";
        parent.appendChild(script);
      }());


    function initHyphenator() {
        window.Hyphenator.config({
            classname: "hyphenate",
            defaultlanguage: "ru-RU",
            displaytogglebox: false,
            doframes: false,
            dohyphenation: true,
            donthyphenateclassname: "donthyphenate",
            enablecache: true,
            hyphenchar: "\u00AD",
            intermediatestate: "hidden",
            minwordlength: 4,
            orphancontrol: 1,
            remoteloading: false,
            safecopy: true,
            storagetype: "localStorage", // localStorage|none|sessionStorage
            useCSS3hyphenation: false,
            unhide: "progressive", // progressive|wait
            urlhyphenchar: "\u200B",
            urlclassname: "urlhyphenate"
        });
        window.Hyphenator.run();
    }

    function init() {
        console.log(55556);
        initHyphenator();
    }

    if (window.addEventListener) {
        window.addEventListener("DOMContentLoaded", function () {
            init();
        }, false);
    } else {
        window.onload = init;
    }
}(window));
